# 最长回文串
def longestPalindrome(s):
    dict = {}
    for x in s:
        if x not in dict:
            dict[x] = 1
        else:
            dict[x] = dict[x] + 1
    else:
        odd = 0
        even = 0
        for value in dict.values():
            if value % 2 != 0:
                odd = odd + 1
                even = 1
        return len(s) - odd + even


# s = ""
s = "abccccdd"
print(longestPalindrome(s))

